let burger=document.querySelector("#burger-icon"),Menu=document.querySelector("#menu"),canSeeMenu=!1;burger.addEventListener("click",(()=>{canSeeMenu?(Menu.classList.remove("show-menu"),canSeeMenu=!1):(Menu.classList.add("show-menu"),canSeeMenu=!0)}));let closeBtn=document.querySelector("#x-button");closeBtn.addEventListener("click",(()=>{Menu.classList.remove("show-menu"),canSeeMenu=!1}));